mapper.select:
SELECT b.*,
-- 댓글 개수 (SELECT COUNT(*) FROM BOARD_COMMENT c WHERE c.BOARD_ID = b.ID) AS commentCount,
-- 내가 좋아요 눌렀는지 여부 CASE WHEN EXISTS ( SELECT 1 FROM BOARD_LIKE l WHERE l.BOARD_ID = b.ID AND l.USER_UID = #{userUid} ) THEN 1 ELSE 0 END AS isLiked
FROM ( SELECT b.* FROM BOARD b
AND b.ID < #{cursorId}
ORDER BY b.ID DESC ) WHERE ROWNUM <= #{limit}

mapper.insert:
INSERT INTO board(id, title, writer_uid, contents, is_notification)
VALUES (
board_seq.nextval, #{title}, #{writerUid}, #{contents}, #{isNotification}
)

    select board_seq.currval from dual

mapper.update:
UPDATE BOARD
SET TITLE           = #{title},
    CONTENT         = #{content},
    IS_NOTIFICATION = #{isNotification}
WHERE ID = #{id}

UPDATE BOARD
SET VIEW_COUNT = VIEW_COUNT + 1
WHERE ID = #{id}
