mapper.insert:
INSERT INTO BOARD_BOOKMARK (
    ID, BOARD_ID, USER_UID
) VALUES (
             BOARD_BOOKMARK_SEQ.NEXTVAL,
             #{boardId},
             #{loginUid}
         )

mapper.delete:
DELETE FROM BOARD_BOOKMARK
WHERE BOARD_ID = #{boardId}
  AND USER_UID = #{loginUid}

mapper.select:
SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END
FROM BOARD_BOOKMARK
WHERE BOARD_ID = #{boardId}
  AND USER_UID = #{loginUid}
